mapper.select:
        SELECT
            CODE_KANJ, CODE_NAME, VALUE1_KANJ, VALUE1, VALUE2_KANJ, VALUE2, VALUE3_KANJ, VALUE3, SORT_NUM, BIKO, DEL_FLG, INS_SHA_YMD, INS_SHA_ID, UPD_SHA_YMD, UPD_SHA_ID
        FROM
            M_RDM_CODE_MST

            AND CODE_KANJ = #{codeKanj}
			AND CODE_NAME = #{codeName}
			AND VALUE1_KANJ = #{value1Kanj}
			AND VALUE1 = #{value1}
			AND VALUE2_KANJ = #{value2Kanj}
			AND VALUE2 = #{value2}
			AND VALUE3_KANJ = #{value3Kanj}
			AND VALUE3 = #{value3}
			AND SORT_NUM = #{sortNum}
			AND BIKO = #{biko}
			AND DEL_FLG = #{delFlg}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

        SELECT
            count (*)
        FROM
            M_RDM_CODE_MST

            AND CODE_KANJ = #{codeKanj}
			AND CODE_NAME = #{codeName}
			AND VALUE1_KANJ = #{value1Kanj}
			AND VALUE1 = #{value1}
			AND VALUE2_KANJ = #{value2Kanj}
			AND VALUE2 = #{value2}
			AND VALUE3_KANJ = #{value3Kanj}
			AND VALUE3 = #{value3}
			AND SORT_NUM = #{sortNum}
			AND BIKO = #{biko}
			AND DEL_FLG = #{delFlg}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

mapper.update:
UPDATE M_RDM_CODE_MST
CODE_KANJ = NULL,CODE_KANJ = #{update.codeKanj}, VALUE1_KANJ = NULL,VALUE1_KANJ = #{update.value1Kanj}, VALUE2_KANJ = NULL,VALUE2_KANJ = #{update.value2Kanj}, VALUE2 = NULL,VALUE2 = #{update.value2}, VALUE3_KANJ = NULL,VALUE3_KANJ = #{update.value3Kanj}, VALUE3 = NULL,VALUE3 = #{update.value3}, SORT_NUM = NULL,SORT_NUM = #{update.sortNum}, BIKO = NULL,BIKO = #{update.biko}, DEL_FLG = NULL,DEL_FLG = #{update.delFlg}, INS_SHA_YMD = NULL,INS_SHA_YMD = #{update.insShaYmd}, INS_SHA_ID = NULL,INS_SHA_ID = #{update.insShaId}, UPD_SHA_YMD = NULL,UPD_SHA_YMD = #{update.updShaYmd}, UPD_SHA_ID = NULL,UPD_SHA_ID = #{update.updShaId},
WHERE CODE_NAME = #{search.codeName} AND VALUE1 = #{search.value1}

mapper.delete:
        DELETE FROM
            M_RDM_CODE_MST

            AND CODE_KANJ = #{codeKanj}
			AND CODE_NAME = #{codeName}
			AND VALUE1_KANJ = #{value1Kanj}
			AND VALUE1 = #{value1}
			AND VALUE2_KANJ = #{value2Kanj}
			AND VALUE2 = #{value2}
			AND VALUE3_KANJ = #{value3Kanj}
			AND VALUE3 = #{value3}
			AND SORT_NUM = #{sortNum}
			AND BIKO = #{biko}
			AND DEL_FLG = #{delFlg}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

mapper.select:
SELECT HCP_WORK.DOC_NO DOC_NO ,HCP_WORK.INS_NO INS_NO FROM M_RDM_HCP_WORK HCP_WORK LEFT JOIN M_RDM_CODE_MST DUMMY_CODE_MST ON DUMMY_CODE_MST.CODE_NAME = 'DUMMY_HCO' AND DUMMY_CODE_MST.DEL_FLG = '0' AND DUMMY_CODE_MST.VALUE1 = HCP_WORK.INS_NO WHERE 1 = 1 AND DUMMY_CODE_MST.VALUE1 IS NULL AND HCP_WORK.DOC_NO = #{inDocNo}
MINUS
SELECT KMU_REQ.DOC_NO DOC_NO ,KMU_REQ.INS_NO_MT INS_NO FROM T_RDM_HCP_KMU_REQ KMU_REQ INNER JOIN T_RDM_REQ_KNR REQ_KNR ON KMU_REQ.REQ_ID = REQ_KNR.REQ_ID AND REQ_KNR.REQ_TYPE = '44' AND REQ_KNR.REQ_STS IN ('03','13','04','14') AND KMU_REQ.DOC_NO = #{inDocNo}
