mapper.select:
        SELECT
            TRT_CD, HIN_G_CD, INS_NO, MAIN_INS_NO, MAIN_INS_FLG, INS_SHA_YMD, INS_SHA_ID, UPD_SHA_YMD, UPD_SHA_ID
        FROM
            M_RDM_HCO_OYAKO

        	AND TRT_CD = #{trtCd}
			AND HIN_G_CD = #{hinGCd}
			AND INS_NO = #{insNo}
			AND MAIN_INS_NO = #{mainInsNo}
			AND MAIN_INS_FLG = #{mainInsFlg}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}
