select.if:
SELECT DISTINCT --部門ランクが3 エリア以下の場合
SOS_MST.BUMON_RANK BUMON_RANK, SOS_MST.SOS_CD SOS_CD, SOS_MST.BUMON_RYAKU_NAME BUMON_RYAKU_NAME, SOS_MST.BR_CODE BR_CODE, SOS_MST.DIST_CODE DIST_CODE, SOS_MST.DIST_SEQ DIST_SEQ,
--部門ランクが3 エリア以下の場合
NULL JGI_NO, NULL JGI_NAME
--部門ランクが4 の場合
JGI_SOS_MST.BUMON_RANK BUMON_RANK, JGI_SOS_MST.SOS_CD SOS_CD, JGI_SOS_MST.BUMON_RYAKU_NAME BUMON_RYAKU_NAME, JGI_SOS_MST.BR_CODE BR_CODE, JGI_SOS_MST.DIST_CODE DIST_CODE, JGI_SOS_MST.DIST_SEQ DIST_SEQ, JGI_SOS_MST.JGI_NO JGI_NO, JGI_SOS_MST.JGI_NAME || '（' || JGI_SOS_MST.SHOKUSHU_NAME || '）'JGI_NAME
--部門ランクが3 エリア以下の場合
FROM M_RDM_SOS_MST SOS_MST --RDM用組織従業員情報 INNER JOIN S_RDM_COM_MR_SOS_TRT COM_MR_SOS ON SOS_MST.SOS_CD = COM_MR_SOS.SOS_CD
--部門ランクが4 の場合
FROM M_RDM_JGI_SOS_MST JGI_SOS_MST --RDM用組織従業員情報 INNER JOIN S_RDM_COM_MR_SOS_TRT COM_MR_SOS --組織領域マスタ ON JGI_SOS_MST.SOS_CD = COM_MR_SOS.SOS_CD INNER JOIN S_RDM_COM_SHOKUSHU_CONFIG COM_SKS_CFG --職種別定義 ON COM_SKS_CFG.SHOKUSHU_CD = JGI_SOS_MST.SHOKUSHU_CD INNER JOIN S_RDM_COM_LNK_TRT_SHOKUSHU LNK_TRT_SKS --領域・職種関連定義 ON LNK_TRT_SKS.TRT_CD = COM_MR_SOS.TRT_CD AND LNK_TRT_SKS.SHOKUSHU_CD = JGI_SOS_MST.SHOKUSHU_CD INNER JOIN S_RDM_COM_LNK_TRTGRP_TRT COM_LNK_TGT --領域グループ・領域関連定義 ON COM_LNK_TGT.TRT_CD = LNK_TRT_SKS.TRT_CD INNER JOIN M_RDM_SOS_MST SOS_MST --RDM用組織情報 ON SOS_MST.SOS_CD = JGI_SOS_MST.SOS_CD INNER JOIN M_JGI_FLG_PLAN FLG_PLAN ON JGI_SOS_MST.JGI_NO = FLG_PLAN.JGI_NO ,M_RDM_COM_CAL_USR RDM_CAL_USR --RDM用カレンダー(オンライン用)
1 = 1
AND RDM_CAL_USR.TODAY = '1' AND LNK_TRT_SKS.TEKIYO_START_DATE <= RDM_CAL_USR.CAL_DATE AND LNK_TRT_SKS.TEKIYO_END_DATE >= RDM_CAL_USR.CAL_DATE AND COM_SKS_CFG.TEKIYO_START_DATE <= RDM_CAL_USR.CAL_DATE AND COM_SKS_CFG.TEKIYO_END_DATE >= RDM_CAL_USR.CAL_DATE AND COM_LNK_TGT.TEKIYO_START_DATE <= RDM_CAL_USR.CAL_DATE AND COM_LNK_TGT.TEKIYO_END_DATE >= RDM_CAL_USR.CAL_DATE AND FLG_PLAN.START_DAY <= RDM_CAL_USR.CAL_YEAR || RDM_CAL_USR.CAL_MONTH || RDM_CAL_USR.CAL_DAY AND FLG_PLAN.END_DAY >= RDM_CAL_USR.CAL_YEAR || RDM_CAL_USR.CAL_MONTH || RDM_CAL_USR.CAL_DAY AND FLG_PLAN.FLG1 = '1' AND JGI_SOS_MST.SOS_CD = #{inUpSosCd}
AND SOS_MST.BUMON_RANK = #{inBumonRank}
AND COM_MR_SOS.TRT_CD = #{inTrtCd}
AND SOS_MST.SOS_CD = #{inSosCd}
AND SOS_MST.UP_SOS_CD = #{inUpSosCd}
AND SOS_MST.SOS_CD = #{inSosCd}
AND SOS_MST.UP_SOS_CD = #{inUpSosCd}
ORDER BY SOS_MST.BR_CODE, SOS_MST.DIST_SEQ, SOS_MST.DIST_CODE
ORDER BY JGI_SOS_MST.BR_CODE, JGI_SOS_MST.DIST_SEQ, JGI_SOS_MST.DIST_CODE, JGI_SOS_MST.JGI_NO
