mapper.select:
        SELECT
            REQ_ID, DOC_NO, CLASS_CATEGORY, PUB_INSTITUTION_CD, PUB_INST_ST_YYYY, PUB_INST_ST_MM, PUB_INST_ST_DD, PUB_INST_ED_YYYY, PUB_INST_ED_MM, PUB_INST_ED_DD, PUB_INST_POSITION, PUB_INSTPOS_ST_YYYY, PUB_INSTPOS_ST_MM, PUB_INSTPOS_ST_DD, PUB_INSTPOS_ED_YYYY, PUB_INSTPOS_ED_MM, PUB_INSTPOS_ED_DD, INS_SHA_YMD, INS_SHA_ID, UPD_SHA_YMD, UPD_SHA_ID
        FROM
            T_RDM_HCP_PUBLIC_REQ

			AND REQ_ID = #{reqId}
			AND DOC_NO = #{docNo}
			AND CLASS_CATEGORY = #{classCategoryCd}
			AND PUB_INSTITUTION_CD = #{pubInstitutionCd}
			AND PUB_INST_ST_YYYY = #{pubInstStYYYY}
			AND PUB_INST_ST_MM = #{pubInstStMM}
			AND PUB_INST_ST_DD = #{pubInstStDD}
			AND PUB_INST_ED_YYYY = #{pubInstEdYYYY}
			AND PUB_INST_ED_MM = #{pubInstEdMM}
			AND PUB_INST_ED_DD = #{pubInstEdDD}
			AND PUB_INST_POSITION = #{pubInstPositionCd}
			AND PUB_INSTPOS_ST_YYYY = #{pubInstposStYYYY}
			AND PUB_INSTPOS_ST_MM = #{pubInstposStMM}
			AND PUB_INSTPOS_ST_DD = #{pubInstposStDD}
			AND PUB_INSTPOS_ED_YYYY = #{pubInstposEdYYYY}
			AND PUB_INSTPOS_ED_MM = #{pubInstposEdMM}
			AND PUB_INSTPOS_ED_DD = #{pubInstposEdDD}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

        SELECT
            count (*)
        FROM
            T_RDM_HCP_PUBLIC_REQ

			AND REQ_ID = #{reqId}
			AND DOC_NO = #{docNo}
			AND CLASS_CATEGORY = #{classCategoryCd}
			AND PUB_INSTITUTION_CD = #{pubInstitutionCd}
			AND PUB_INST_ST_YYYY = #{pubInstStYYYY}
			AND PUB_INST_ST_MM = #{pubInstStMM}
			AND PUB_INST_ST_DD = #{pubInstStDD}
			AND PUB_INST_ED_YYYY = #{pubInstEdYYYY}
			AND PUB_INST_ED_MM = #{pubInstEdMM}
			AND PUB_INST_ED_DD = #{pubInstEdDD}
			AND PUB_INST_POSITION = #{pubInstPositionCd}
			AND PUB_INSTPOS_ST_YYYY = #{pubInstposStYYYY}
			AND PUB_INSTPOS_ST_MM = #{pubInstposStMM}
			AND PUB_INSTPOS_ST_DD = #{pubInstposStDD}
			AND PUB_INSTPOS_ED_YYYY = #{pubInstposEdYYYY}
			AND PUB_INSTPOS_ED_MM = #{pubInstposEdMM}
			AND PUB_INSTPOS_ED_DD = #{pubInstposEdDD}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

mapper.update:
UPDATE T_RDM_HCP_PUBLIC_REQ
DOC_NO = NULL,DOC_NO = #{update.docNo}, CLASS_CATEGORY = NULL,CLASS_CATEGORY = #{update.classCategoryCd}, PUB_INST_ST_YYYY = NULL,PUB_INST_ST_YYYY = #{update.pubInstStYYYY}, PUB_INST_ST_MM = NULL,PUB_INST_ST_MM = #{update.pubInstStMM}, PUB_INST_ST_DD = NULL,PUB_INST_ST_DD = #{update.pubInstStDD}, PUB_INST_ED_YYYY = NULL,PUB_INST_ED_YYYY = #{update.pubInstEdYYYY}, PUB_INST_ED_MM = NULL,PUB_INST_ED_MM = #{update.pubInstEdMM}, PUB_INST_ED_DD = NULL,PUB_INST_ED_DD = #{update.pubInstEdDD}, PUB_INST_POSITION = NULL,PUB_INST_POSITION = #{update.pubInstPositionCd}, PUB_INSTPOS_ST_YYYY = NULL,PUB_INSTPOS_ST_YYYY = #{update.pubInstposStYYYY}, PUB_INSTPOS_ST_MM = NULL,PUB_INSTPOS_ST_MM = #{update.pubInstposStMM}, PUB_INSTPOS_ST_DD = NULL,PUB_INSTPOS_ST_DD = #{update.pubInstposStDD}, PUB_INSTPOS_ED_YYYY = NULL,PUB_INSTPOS_ED_YYYY = #{update.pubInstposEdYYYY}, PUB_INSTPOS_ED_MM = NULL,PUB_INSTPOS_ED_MM = #{update.pubInstposEdMM}, PUB_INSTPOS_ED_DD = NULL,PUB_INSTPOS_ED_DD = #{update.pubInstposEdDD}, INS_SHA_YMD = NULL,INS_SHA_YMD = #{update.insShaYmd}, INS_SHA_ID = NULL,INS_SHA_ID = #{update.insShaId}, UPD_SHA_YMD = NULL,UPD_SHA_YMD = #{update.updShaYmd}, UPD_SHA_ID = NULL,UPD_SHA_ID = #{update.updShaId},
WHERE REQ_ID = #{reqId} AND PUB_INSTITUTION_CD = #{pubInstitutionCd}

