mapper.select:
        SELECT
            REQ_ID, DOC_NO, DOC_TYPE, DOC_KANJ, DOC_KANA, DOC_KANJ_SEI, DOC_KANJ_MEI, DOC_KANA_SEI, DOC_KANA_MEI, OLD_KANJ_SEI, OLD_KANA_SEI, NEW_NAME_ST_YYYY, NEW_NAME_ST_MM, NEW_NAME_ST_DD, DOB_YYYY, DOB_MM, DOB_DD, SEX, HOME_TOWN, MED_SCH, GRAD_YEAR, HOME_DEPT_CODE, H_UNIV, EMPL_YEAR, SP_LIVER, SP_DISEASE, SP_COM, INS_NO_SK, JOB_FORM, DEPT_CODE, UNIV_POS_CODE, TITLE_CODE, DCC_TYPE, ULT_DOC_NO, MDM_ID, DOC_ATTRIBUTE, DEL_REASON, DEL_YMD, DUP_DOC_NO, RST_REASON, INS_SHA_YMD, INS_SHA_ID, UPD_SHA_YMD, UPD_SHA_ID
        FROM
            T_RDM_HCP_REQ

			AND REQ_ID = #{reqId}
			AND DOC_NO = #{docNo}
			AND DOC_TYPE = #{docType}
			AND DOC_KANJ = #{docKanj}
			AND DOC_KANA = #{docKana}
			AND DOC_KANJ_SEI = #{docKanjiSei}
			AND DOC_KANJ_MEI = #{docKanjiMei}
			AND DOC_KANA_SEI = #{docKanaSei}
			AND DOC_KANA_MEI = #{docKanaMei}
			AND OLD_KANJ_SEI = #{oldKanjSei}
			AND OLD_KANA_SEI = #{oldKanaSei}
			AND NEW_NAME_ST_YYYY = #{newNameStYear}
			AND NEW_NAME_ST_MM = #{newNameStMonth}
			AND NEW_NAME_ST_DD = #{newNameStDay}
			AND DOB_YYYY = #{dobYear}
			AND DOB_MM = #{dobMonth}
			AND DOB_DD = #{dobDay}
			AND SEX = #{sexCd}
			AND HOME_TOWN = #{homeTownCd}
			AND MED_SCH = #{medSchoolCd}
			AND GRAD_YEAR = #{gradYear}
			AND HOME_DEPT_CODE = #{homeDeptCd}
			AND H_UNIV = #{homeUnivCd}
			AND EMPL_YEAR = #{emplYear}
			AND SP_LIVER = #{spLiverCd}
			AND SP_DISEASE = #{spDiseaseCd}
			AND SP_COM = #{spCom}
			AND INS_NO_SK = #{skInsNo}
			AND JOB_FORM = #{skJobForm}
			AND DEPT_CODE = #{skDeptCd}
			AND UNIV_POS_CODE = #{skUnivPosCd}
			AND TITLE_CODE = #{skTitleCd}
			AND DCC_TYPE = #{skDcctype}
			AND ULT_DOC_NO = #{ultDocCd}
			AND MDM_ID = #{mdmId}
			AND DOC_ATTRIBUTE = #{docAttribute}
			AND DEL_REASON = #{delReason}
			AND DEL_YMD = #{delYMD}
			AND DUP_DOC_NO = #{dupDocNo}
			AND RST_REASON = #{rstReason}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

        SELECT
            count (*)
        FROM
            T_RDM_HCP_REQ

			AND REQ_ID = #{reqId}
			AND DOC_NO = #{docNo}
			AND DOC_TYPE = #{docType}
			AND DOC_KANJ = #{docKanj}
			AND DOC_KANA = #{docKana}
			AND DOC_KANJ_SEI = #{docKanjiSei}
			AND DOC_KANJ_MEI = #{docKanjiMei}
			AND DOC_KANA_SEI = #{docKanaSei}
			AND DOC_KANA_MEI = #{docKanaMei}
			AND OLD_KANJ_SEI = #{oldKanjSei}
			AND OLD_KANA_SEI = #{oldKanaSei}
			AND NEW_NAME_ST_YYYY = #{newNameStYear}
			AND NEW_NAME_ST_MM = #{newNameStMonth}
			AND NEW_NAME_ST_DD = #{newNameStDay}
			AND DOB_YYYY = #{dobYear}
			AND DOB_MM = #{dobMonth}
			AND DOB_DD = #{dobDay}
			AND SEX = #{sexCd}
			AND HOME_TOWN = #{homeTownCd}
			AND MED_SCH = #{medSchoolCd}
			AND GRAD_YEAR = #{gradYear}
			AND HOME_DEPT_CODE = #{homeDeptCd}
			AND H_UNIV = #{homeUnivCd}
			AND EMPL_YEAR = #{emplYear}
			AND SP_LIVER = #{spLiverCd}
			AND SP_DISEASE = #{spDiseaseCd}
			AND SP_COM = #{spCom}
			AND INS_NO_SK = #{skInsNo}
			AND JOB_FORM = #{skJobForm}
			AND DEPT_CODE = #{skDeptCd}
			AND UNIV_POS_CODE = #{skUnivPosCd}
			AND TITLE_CODE = #{skTitleCd}
			AND DCC_TYPE = #{skDcctype}
			AND ULT_DOC_NO = #{ultDocCd}
			AND MDM_ID = #{mdmId}
			AND DOC_ATTRIBUTE = #{docAttribute}
			AND DEL_REASON = #{delReason}
			AND DEL_YMD = #{delYMD}
			AND DUP_DOC_NO = #{dupDocNo}
			AND RST_REASON = #{rstReason}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

mapper.update:
