mapper.select:
        SELECT
            REQ_ID, DOC_NO, MEDICAL_SOCIETY_NM, ADMISSION_YYYY, ADMISSION_MM, ADMISSION_DD, QUIT_YYYY, QUIT_MM, QUIT_DD, POSITION_CODE, SOCIETY_POSI_ST_YYYY, SOCIETY_POSI_ST_MM, SOCIETY_POSI_ST_DD, SOCIETY_POSI_ED_YYYY, SOCIETY_POSI_ED_MM, SOCIETY_POSI_ED_DD, ADVISING_DOCTOR, COACHING_ACQUISI_YYYY, COACHING_ACQUISI_MM, COACHING_ACQUISI_DD, COACHING_ST_YYYY, COACHING_ST_MM, COACHING_ST_DD, COACHING_ED_YYYY, COACHING_ED_MM, COACHING_ED_DD, CERTIFYING_PHYSICIAN, CERTIFY_ST_YYYY, CERTIFY_ST_MM, CERTIFY_ST_DD, CERTIFY_ED_YYYY, CERTIFY_ED_MM, CERTIFY_ED_DD, INS_SHA_YMD, INS_SHA_ID, UPD_SHA_YMD, UPD_SHA_ID
        FROM
            T_RDM_HCP_SOCIETY_REQ

			AND REQ_ID = #{reqId}
			AND DOC_NO = #{docNo}
			AND MEDICAL_SOCIETY_NM = #{medicalSocietyNm}
			AND ADMISSION_YYYY = #{admissionYYYY}
			AND ADMISSION_MM = #{admissionMM}
			AND ADMISSION_DD = #{admissionDD}
			AND QUIT_YYYY = #{quitYYYY}
			AND QUIT_MM = #{quitMM}
			AND QUIT_DD = #{quitDD}
			AND POSITION_CODE = #{positionCode}
			AND SOCIETY_POSI_ST_YYYY = #{societyPosiStYYYY}
			AND SOCIETY_POSI_ST_MM = #{societyPosiStMM}
			AND SOCIETY_POSI_ST_DD = #{societyPosiStDD}
			AND SOCIETY_POSI_ED_YYYY = #{societyPosiEdYYYY}
			AND SOCIETY_POSI_ED_MM = #{societyPosiEdMM}
			AND SOCIETY_POSI_ED_DD = #{societyPosiEdDD}
			AND ADVISING_DOCTOR = #{advisingDoctorCd}
			AND COACHING_ACQUISI_YYYY = #{coachingAcquisiYYYY}
			AND COACHING_ACQUISI_MM = #{coachingAcquisiMM}
			AND COACHING_ACQUISI_DD = #{coachingAcquisiDD}
			AND COACHING_ST_YYYY = #{coachingStYYYY}
			AND COACHING_ST_MM = #{coachingStMM}
			AND COACHING_ST_DD = #{coachingStDD}
			AND COACHING_ED_YYYY = #{coachingEdYYYY}
			AND COACHING_ED_MM = #{coachingEdMM}
			AND COACHING_ED_DD = #{coachingEdDD}
			AND CERTIFYING_PHYSICIAN = #{certifyingPhysicianCd}
			AND CERTIFY_ST_YYYY = #{certifyStYYYY}
			AND CERTIFY_ST_MM = #{certifyStMM}
			AND CERTIFY_ST_DD = #{certifyStDD}
			AND CERTIFY_ED_YYYY = #{certifyEdYYYY}
			AND CERTIFY_ED_MM = #{certifyEdMM}
			AND CERTIFY_ED_DD = #{certifyEdDD}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

        SELECT
            count (*)
        FROM
            T_RDM_HCP_SOCIETY_REQ

			AND REQ_ID = #{reqId}
			AND DOC_NO = #{docNo}
			AND MEDICAL_SOCIETY_NM = #{medicalSocietyNm}
			AND ADMISSION_YYYY = #{admissionYYYY}
			AND ADMISSION_MM = #{admissionMM}
			AND ADMISSION_DD = #{admissionDD}
			AND QUIT_YYYY = #{quitYYYY}
			AND QUIT_MM = #{quitMM}
			AND QUIT_DD = #{quitDD}
			AND POSITION_CODE = #{positionCode}
			AND SOCIETY_POSI_ST_YYYY = #{societyPosiStYYYY}
			AND SOCIETY_POSI_ST_MM = #{societyPosiStMM}
			AND SOCIETY_POSI_ST_DD = #{societyPosiStDD}
			AND SOCIETY_POSI_ED_YYYY = #{societyPosiEdYYYY}
			AND SOCIETY_POSI_ED_MM = #{societyPosiEdMM}
			AND SOCIETY_POSI_ED_DD = #{societyPosiEdDD}
			AND ADVISING_DOCTOR = #{advisingDoctorCd}
			AND COACHING_ACQUISI_YYYY = #{coachingAcquisiYYYY}
			AND COACHING_ACQUISI_MM = #{coachingAcquisiMM}
			AND COACHING_ACQUISI_DD = #{coachingAcquisiDD}
			AND COACHING_ST_YYYY = #{coachingStYYYY}
			AND COACHING_ST_MM = #{coachingStMM}
			AND COACHING_ST_DD = #{coachingStDD}
			AND COACHING_ED_YYYY = #{coachingEdYYYY}
			AND COACHING_ED_MM = #{coachingEdMM}
			AND COACHING_ED_DD = #{coachingEdDD}
			AND CERTIFYING_PHYSICIAN = #{certifyingPhysicianCd}
			AND CERTIFY_ST_YYYY = #{certifyStYYYY}
			AND CERTIFY_ST_MM = #{certifyStMM}
			AND CERTIFY_ST_DD = #{certifyStDD}
			AND CERTIFY_ED_YYYY = #{certifyEdYYYY}
			AND CERTIFY_ED_MM = #{certifyEdMM}
			AND CERTIFY_ED_DD = #{certifyEdDD}
			AND INS_SHA_YMD = #{insShaYmd}
			AND INS_SHA_ID = #{insShaId}
			AND UPD_SHA_YMD = #{updShaYmd}
			AND UPD_SHA_ID = #{updShaId}

mapper.update:
UPDATE T_RDM_HCP_SOCIETY_REQ
DOC_NO = NULL,DOC_NO = #{update.docNo}, ADMISSION_YYYY = NULL,ADMISSION_YYYY = #{update.admissionYYYY}, ADMISSION_MM = NULL,ADMISSION_MM = #{update.admissionMM}, ADMISSION_DD = NULL,ADMISSION_DD = #{update.admissionDD}, QUIT_YYYY = NULL,QUIT_YYYY = #{update.quitYYYY}, QUIT_MM = NULL,QUIT_MM = #{update.quitMM}, QUIT_DD = NULL,QUIT_DD = #{update.quitDD}, POSITION_CODE = NULL,POSITION_CODE = #{update.positionCode}, SOCIETY_POSI_ST_YYYY = NULL,SOCIETY_POSI_ST_YYYY = #{update.societyPosiStYYYY}, SOCIETY_POSI_ST_MM = NULL,SOCIETY_POSI_ST_MM = #{update.societyPosiStMM}, SOCIETY_POSI_ST_DD = NULL,SOCIETY_POSI_ST_DD = #{update.societyPosiStDD}, SOCIETY_POSI_ED_YYYY = NULL,SOCIETY_POSI_ED_YYYY = #{update.societyPosiEdYYYY}, SOCIETY_POSI_ED_MM = NULL,SOCIETY_POSI_ED_MM = #{update.societyPosiEdMM}, SOCIETY_POSI_ED_DD = NULL,SOCIETY_POSI_ED_DD = #{update.societyPosiEdDD}, ADVISING_DOCTOR = NULL,ADVISING_DOCTOR = #{update.advisingDoctorCd}, COACHING_ACQUISI_YYYY = NULL,COACHING_ACQUISI_YYYY = #{update.coachingAcquisiYYYY}, COACHING_ACQUISI_MM = NULL,COACHING_ACQUISI_MM = #{update.coachingAcquisiMM}, COACHING_ACQUISI_DD = NULL,COACHING_ACQUISI_DD = #{update.coachingAcquisiDD}, COACHING_ST_YYYY = NULL,COACHING_ST_YYYY = #{update.coachingStYYYY}, COACHING_ST_MM = NULL,COACHING_ST_MM = #{update.coachingStMM}, COACHING_ST_DD = NULL,COACHING_ST_DD = #{update.coachingStDD}, COACHING_ED_YYYY = NULL,COACHING_ED_YYYY = #{update.coachingEdYYYY}, COACHING_ED_MM = NULL,COACHING_ED_MM = #{update.coachingEdMM}, COACHING_ED_DD = NULL,COACHING_ED_DD = #{update.coachingEdDD}, CERTIFYING_PHYSICIAN = NULL,CERTIFYING_PHYSICIAN = #{update.certifyingPhysicianCd}, CERTIFY_ST_YYYY = NULL,CERTIFY_ST_YYYY = #{update.certifyStYYYY}, CERTIFY_ST_MM = NULL,CERTIFY_ST_MM = #{update.certifyStMM}, CERTIFY_ST_DD = NULL,CERTIFY_ST_DD = #{update.certifyStDD}, CERTIFY_ED_YYYY = NULL,CERTIFY_ED_YYYY = #{update.certifyEdYYYY}, CERTIFY_ED_MM = NULL,CERTIFY_ED_MM = #{update.certifyEdMM}, CERTIFY_ED_DD = NULL,CERTIFY_ED_DD = #{update.certifyEdDD}, INS_SHA_YMD = NULL,INS_SHA_YMD = #{update.insShaYmd}, INS_SHA_ID = NULL,INS_SHA_ID = #{update.insShaId}, UPD_SHA_YMD = NULL,UPD_SHA_YMD = #{update.updShaYmd}, UPD_SHA_ID = NULL,UPD_SHA_ID = #{update.updShaId},
WHERE REQ_ID = #{reqId} AND MEDICAL_SOCIETY_NM = #{medicalSocietyNm}

